            // - - - -> position.x      |
            // -> bbox.x
            //                   |
            //         
            //                 |
            //   
            //                  |
            //   <-dx->                     | restricted area right border
            //         <-width->        |    translated element
            //   <- - bbox.width - ->        embedded element
            var dx = position.x - bbox.x;
            var dy = position.y - bbox.y;
